import React, { useState } from 'react';

function checkmail(mail) {
    let mailformat = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;
    if (mail.match(mailformat)){
        return null;
    }
    else{
        const checkmail = "Email Id is invalid";
    }
}

export default checkmail;
