import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import * as React from "react";
import { styled, alpha } from "@mui/material/styles";
import AppBar from "@mui/material/AppBar";
import Box from "@mui/material/Box";
import Toolbar from "@mui/material/Toolbar";
import IconButton from "@mui/material/IconButton";
import Typography from "@mui/material/Typography";
import InputBase from "@mui/material/InputBase";
import Badge from "@mui/material/Badge";
import MenuItem from "@mui/material/MenuItem";
import Menu from "@mui/material/Menu";
import MenuIcon from "@mui/icons-material/Menu";
import SearchIcon from "@mui/icons-material/Search";
import AccountCircle from "@mui/icons-material/AccountCircle";
import MailIcon from "@mui/icons-material/Mail";
import NotificationsIcon from "@mui/icons-material/Notifications";
import MoreIcon from "@mui/icons-material/MoreVert";
import { display } from "@mui/system";

const searchStyle = {display:'flex' , justifyContent:'center'};

const Home = () => {
  // const sendGetRequest = async () => {

  //     try {
  //         const response = await axios.get('https://therecipepool.pythonanywhere.com/api/favourite/'
  //         ,{
  //             headers:{
  //                 'Authorization' : 'Bearer' + localStorage.getItem('accessToken'),
  //                 'Content-Type': 'application/x-www-form-urlencoded'
  //             }
  //         })

  //         console.log(response.data);

  //     } catch (err) {
  //         console.log("error in sendGetRequest",err);
  //     }
  // };

  // sendGetRequest();

  const Search = styled("div")(({ theme }) => ({
    position: "relative",
    borderRadius: theme.shape.borderRadius,
    backgroundColor: alpha(theme.palette.common.white, 0.15),
    "&:hover": {
      backgroundColor: alpha(theme.palette.common.white, 0.25),
    },
    marginLeft: 0,
    width: "100%",
    [theme.breakpoints.up("sm")]: {
      marginLeft: theme.spacing(1),
      width: "auto",
    },
  }));

  const SearchIconWrapper = styled("div")(({ theme }) => ({
    padding: theme.spacing(0, 2),
    height: "100%",
    position: "absolute",
    pointerEvents: "none",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  }));

  const StyledInputBase = styled(InputBase)(({ theme }) => ({
    color: "inherit",
    "& .MuiInputBase-input": {
      padding: theme.spacing(1, 1, 1, 0),
      // vertical padding + font size from searchIcon
      paddingLeft: `calc(1em + ${theme.spacing(4)})`,
      transition: theme.transitions.create("width"),
      width: "100%",
      [theme.breakpoints.up("sm")]: {
        width: "20ch",
        "&:focus": {
          width: "20ch",
        },
      },
    },
  }));

  return (
    <Box sx={{ flexGrow: 1 }}>
      <AppBar position="static"
      sx={{background: 
        "url('https://i.pinimg.com/564x/33/ef/8b/33ef8b9c0b902154a6cd4103a21275ef.jpg') no-repeat center ", 
        backgroundSize:'cover',
        height:'170px'}}>
        <Toolbar style={searchStyle}>

          <Search  sx={{borderRadius:'10rem', color:'#607d8b', border:'1px solid black'}}>
            <SearchIconWrapper>
              <SearchIcon />
            </SearchIconWrapper>
            <StyledInputBase
              placeholder="Search Recipes…"
              inputProps={{ "aria-label": "search" }}
              sx = {{width:'30rem'}}
            />
          </Search>
        </Toolbar>
        <p style={{display:'flex', alignItems:'center', fontSize:'2rem', color:'black'}}>Explore Recipes</p>
      </AppBar>
      
    </Box>
  );
};

export default Home;
