import React , { useState, useEffect } from "react";
import { Link } from "react-router-dom";

// import changePg from './index';
import {
  Grid,
  Paper,
  Avatar,
  TextField,
  FormControlLabel,
  Checkbox,
  Button,
  Typography,
} from "@mui/material";
import LockIcon from "@mui/icons-material/Lock";

const Login = () => {
  const paperStyle = {
    padding: 20,
    height: "70vh",
    width: "400px",
    margin: "20px auto",
  };
  const avatarStyle = { backgroundColor: "green" };
  const textFieldStyle = { margin: "10px" };
  const checkboxStyle = { position: "relative", right: "28%" };
  const btnStyle = {margin:'5px  '}
  const errorStyle = {display:"flex" , align:"left" ,margin:'-1px', color:'red' }

  const initialValues = {username:"",  password:""};

  const [formValues , setFormValues] = useState(initialValues);
  const [formErrors , setFormErrors] = useState({});
  const [isSubmit , setIsSubmit] = useState(false);

  const handleChange = (e) => {
    // console.log(e.target);
    // console.log(e.target.value);
    const {name , value} = e.target;
    setFormValues({...formValues , [name]: value});
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setFormErrors(validateForm(formValues));
    setIsSubmit(true);
  };

  useEffect(() => {
    console.log(formErrors);
    if(Object.keys(formErrors).length === 0 && isSubmit){
      console.log(formValues);
    }
  }, [formErrors])

  const validateForm = (formValues) => {
    const errors = {};
    const regex_password = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)[a-zA-Z\d]{8,}$/;
    if(!formValues.username) {
      errors.username = 'Username is required!';
    }
    if(!formValues.email) {
      errors.email = 'Email is required!';
    }
    if(!formValues.password) {
      errors.password = 'Password is required!';
    }
    else if(!formValues.password.match(regex_password)){
      errors.password = 'Password must contain min 8 characters, 1 uppercase, 1 lowercase & 1 number';
    }
    return errors;
    
  };

  return (
    <Grid>
      <Paper elevation={10} style={paperStyle}>
        <Grid align="center">
          <Avatar style={avatarStyle}>
            <LockIcon />
          </Avatar>
          <h2>Sign In</h2>

          <TextField
            id="outlined-basic"
            label="Username"
            placeholder="Enter Username"
            variant="outlined"
            fullWidth
            required
            style={textFieldStyle}
            name='username'
            onChange={handleChange}
            value={formValues.username}
          />
          <p style={errorStyle}>{formErrors.username}</p>
          <TextField
            id="outlined-basic"
            label="Password"
            placeholder="Enter password"
            type="password"
            variant="outlined"
            fullWidth
            required
            style={textFieldStyle}
            name='password'
            onChange={handleChange}
            value={formValues.password}
          />
          <p style={errorStyle}>{formErrors.password}</p>

          <FormControlLabel
            control={<Checkbox color="success" />}
            label="Remember me"
            style={checkboxStyle}
          />

          <Button variant="contained" color="success" style={btnStyle} fullWidth onClick={handleChange}>
            SIGN IN
          </Button>

        <Typography>
          <Link to="#" underline="hover">
            Forgot password ?
          </Link>
          </Typography>

        <Typography>
          Do you have an account ? 
          <Link to="/signup" underline="hover ">
            Sign Up
          </Link>
          </Typography>
        </Grid>
      </Paper>
    </Grid>
  )
}
export default Login;
