import React, { useState, useEffect, useRef } from 'react'; 
import {
  Grid,
  Paper,
  Avatar,
  TextField,
  FormControlLabel,
  Checkbox,
  Link ,
  Button,
  Typography,FormControl , FormLabel , RadioGroup , Radio 
} from "@mui/material";
import AddCircleOutlinedIcon from '@mui/icons-material/AddCircleOutlined';

const Signup = () => {
  const paperStyle = {
    padding: 20,
    height: "87vh",
    width: "400px",
    margin: "20px auto",
  };
  const avatarStyle = { backgroundColor: "green" };
  const textFieldStyle = { margin: "10px" };
  // const checkboxStyle = { position: "relative", right: "28%" };
  const btnStyle = { margin: "5px  " };
  const numberStyle = {position:'relative', left:'2.5%'}
  const radioStyle={position:'relative', right:'18%', margin:'4px'};
  const genderStyle={position:'relative', left:'-57%', margin:'2px'};

  const initialValues = {username:"", email:"", password:""};

  const [formValues , setFormValues] = useState(initialValues);

    const handleChange = (e) => {
      console.log(e.target);
      // console.log(e.target.value);
      const {name , value} = e.target;
      setFormValues({...formValues , [name]: value});
      console.log(formValues);
    };

    const handleSubmit = (e) => {
      e.preventDefault();
    };



  return (
    <Grid>
      <form onSubmit={handleSubmit}>
      
      <Paper elevation={10} style={paperStyle}>
        <Grid align="center">
          <Avatar style={avatarStyle}>
            <AddCircleOutlinedIcon />
          </Avatar>
          <h2>Sign Up</h2>
          

          <TextField
            id="outlined-basic"
            label="Name"
            name='username'
            placeholder="Enter Name"
            variant="outlined"
            fullWidth
            required
            style={textFieldStyle}
            onChange={handleChange}
            value={formValues.username}
          />
          <TextField
            id="outlined-basic signupmail"
            label="Email"
            name='email'
            placeholder="Enter Email"
            variant="outlined"
            type="email"
            fullWidth
            required
            style={textFieldStyle}
            value={formValues.email}
            onChange={handleChange}
          />

<FormControl>
      <FormLabel id="demo-row-radio-buttons-group-label" style={genderStyle}>Gender</FormLabel >
      <RadioGroup
        row
        aria-labelledby="demo-row-radio-buttons-group-label"
        name="row-radio-buttons-group"
        style={radioStyle}
      >
        <FormControlLabel value="female" control={<Radio />} label="Female" />
        <FormControlLabel value="male" control={<Radio />} label="Male" />
        <FormControlLabel value="other" control={<Radio />} label="Other" />
      </RadioGroup>
    </FormControl>

    <TextField
          id="outlined-number"
          label="Number"
          type="number"
          fullWidth
          required
          style={numberStyle}
          InputLabelProps={{
            shrink: true,
          }}
        />

<TextField
            id="outlined-basic"
            label="Password"
            name='password'
            placeholder="Enter password"
            type="password"
            variant="outlined"
            fullWidth
            required
            style={textFieldStyle}
            value={formValues.password}
            onChange={handleChange}
          />

        {/* <FormControl sx={{ m: 1, width: '25ch' }} variant="outlined">
          <InputLabel htmlFor="outlined-adornment-password">Password</InputLabel>
          <OutlinedInput
            id="outlined-adornment-password"
            type={values.showPassword ? 'text' : 'password'}
            value={values.password}
            onChange={handleChange('password')}
            endAdornment={
              <InputAdornment position="end">
                <IconButton
                  aria-label="toggle password visibility"
                  onClick={handleClickShowPassword}
                  onMouseDown={handleMouseDownPassword}
                  edge="end"
                >
                  {values.showPassword ? <VisibilityOff /> : <Visibility />}
                </IconButton>
              </InputAdornment>
            }
            label="Password"
          />
        </FormControl> */}

          <TextField
            id="outlined-basic"
            label="Confirm Password"
            placeholder="Confirm password"
            type="password"
            variant="outlined"
            fullWidth
            required
            style={textFieldStyle}
          />


          <Button
            variant="contained"
            color="success"
            style={btnStyle}
            fullWidth
          >
            SIGN UP
          </Button>
        </Grid>
      </Paper>
      </form>
    </Grid>
  )
}
export default Signup;
